chrome.runtime.onMessage.addListener((e=>{switch(e.task){case"open website":chrome.tabs.create({url:e.link},(s=>{chrome.tabs.executeScript(s.id,{code:e.code})}));break;case"update keep":chrome.tabs.query({},(e=>{e.forEach((e=>{chrome.tabs.sendMessage(e.id,{task:"update keep"})}))}))}})),chrome.browserAction.onClicked.addListener((e=>{chrome.tabs.sendMessage(e.id,{task:"click icon"})}));